import styled from "styled-components";

export const SignInContainer = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  border: solid black 1px;
  padding-top: 10px;

  h2 {
    font-size: 30px;
    font-weight: bold;
    margin: 10px 0px;
  }

  span {
    font-size: 20px;
    font-weight: normal;
    opacity: 0.86;
  }
`;

export const ButtonsBarContainer = styled.div`
  display: flex;
  justify-content: space-between;
`;
