import React from "react";
// import "./checkout.scss";

import { connect } from "react-redux";
import {
  selectCartItems,
  selectItemsCount,
  selectCartTotal,
} from "./../../redux/cart/cart.selectors";
import { createStructuredSelector } from "reselect";

const CheckoutPage = ({ cartItems, totalPrice }) => {
  return (
    <div
      className="checkout-page"
      style={{
        width: "55%",
        border: "solid blue",
        display: "flex",
        flexDirection: "column",
        margin: "50px auto 0",
      }}
    >
      <div
        className="checkout-header"
        style={{
          display: "flex",
          margin: "30px auto",
          width: "100%",
          justifyContent: "space-between",
        }}
      >
        <div
          className="header-block"
          style={{ width: "23%", display: "flex", flexDirection: "column" }}
        >
          <span>Product</span>
          {cartItems.map((cartItem) => (
            <span key={cartItem.id}>
              <img src={cartItem.imageUrl} alt="" style={{ width: "30px" }} />
            </span>
          ))}
        </div>
        <div className="header-block" style={{ width: "23%" }}>
          <span>Description</span>
        </div>
        <div className="header-block" style={{ width: "23%" }}>
          <span>Quantity</span>
        </div>
        <div className="header-block" style={{ width: "23%" }}>
          <span>Price</span>
        </div>
        <div className="header-block" style={{ width: "8%" }}>
          <span>Remove</span>
        </div>
      </div>

      <div className="bottom">{totalPrice}</div>
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItems,
  totalPrice: selectCartTotal,
});
export default connect(mapStateToProps)(CheckoutPage);
