import React, { Component } from "react";
import { Route } from "react-router-dom";
import CollectionPage from "../collection/collection";
import { connect } from "react-redux";
import { fetchCollectionsStartAsync } from "./../../redux/shop/shop.actions";
import WithSpinner from "./../../components/withSpinner/withSpinner";
import CollectionOverview from "./../../components/collectionsOverview/collectionsOverview";
import { createStructuredSelector } from "reselect";
import { selectIsCollectionFetching } from "./../../redux/shop/shop.selectors";

const CollectionOverViewSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }

  render() {
    const { match, isCollectionFetching } = this.props;

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverViewSpinner
              isLoading={isCollectionFetching}
              {...props}
            />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionPageWithSpinner
              isLoading={isCollectionFetching}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () =>
    dispatch(fetchCollectionsStartAsync(collectionsMap)),
});

const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectIsCollectionFetching,
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
